import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useEmail } from '../contexts/EmailContext';
import { useSocket } from '../contexts/SocketContext';
import Header from './Header';
import EmailAddressDisplay from './EmailAddressDisplay';
import EmailList from './EmailList';
import EmailSearch from './EmailSearch';
import EmailActions from './EmailActions';
import LoadingSpinner from './LoadingSpinner';

const HomePage = () => {
  const navigate = useNavigate();
  const { 
    currentEmailAddress, 
    emails, 
    loading, 
    error,
    generateEmailAddress, 
    fetchEmails,
    markEmailAsRead 
  } = useEmail();
  const { isConnected } = useSocket();

  // Generate email address on component mount if none exists
  useEffect(() => {
    if (!currentEmailAddress && !loading) {
      generateEmailAddress();
    }
  }, []); // Only run once on mount

  // Fetch emails when email address changes
  useEffect(() => {
    if (currentEmailAddress) {
      fetchEmails();
    }
  }, [currentEmailAddress, fetchEmails]);

  const handleEmailClick = async (email) => {
    if (!email.read) {
      await markEmailAsRead(email.id);
    }
    navigate(`/email/${email.id}`);
  };

  const handleGenerateNewAddress = () => {
    generateEmailAddress();
  };

  if (loading && !currentEmailAddress) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <LoadingSpinner />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <main className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          {/* Connection Status */}
          <div className={`mb-4 p-3 rounded-lg text-sm ${
            isConnected 
              ? 'bg-green-100 text-green-800' 
              : 'bg-red-100 text-red-800'
          }`}>
            {isConnected ? 'ğŸŸ¢ Connected' : 'ğŸ”´ Disconnected'}
          </div>

          {/* Email Address Display */}
          <EmailAddressDisplay 
            emailAddress={currentEmailAddress}
            onGenerateNew={handleGenerateNewAddress}
          />

          {/* Search Bar */}
          <EmailSearch />

          {/* Email Actions */}
          <EmailActions />

          {/* Error Display */}
          {error && (
            <div className="mb-6 p-4 bg-red-100 border border-red-400 text-red-700 rounded-lg">
              {error}
            </div>
          )}

          {/* Email List */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200">
            <div className="p-6 border-b border-gray-200">
              <h2 className="text-xl font-semibold text-gray-900">
                Inbox {emails.length > 0 && `(${emails.length})`}
              </h2>
            </div>

            {loading ? (
              <div className="p-8 text-center">
                <LoadingSpinner />
              </div>
            ) : emails.length === 0 ? (
              <div className="p-8 text-center text-gray-500">
                <div className="text-6xl mb-4">ğŸ“§</div>
                <p className="text-lg">No emails yet</p>
                <p className="text-sm">Emails sent to your address will appear here</p>
              </div>
            ) : (
              <EmailList 
                emails={emails} 
                onEmailClick={handleEmailClick}
              />
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default HomePage;
